function get_color(data)
{
    var c = Math.round(color(data));
  
    if (isNaN(c))
        return '#dddddd';	// fallback color
    
    return colors[c];
}

// Can't just use d3.svg.diagonal because one edge is in normal space, the
// other edge is in radial space. Since we can't just ask d3 to do projection
// of a single point, do it ourselves the same way d3 would do it.  

// //console.log(data)

function projectX(x)
{
    return ((x - 90) / 180 * Math.PI) - (Math.PI/2);
}

var diagonal = d3.svg.diagonal()
    .source(function(d) { return {"x": typeof(d.right) !== "undefined" ? d.right.y * Math.cos(projectX(d.right.x)) : d.left.y * Math.cos(projectX(d.left.x)), 
                                  "y": typeof(d.right) !== "undefined" ? -d.right.y * Math.sin(projectX(d.right.x)) : -d.left.y * Math.sin(projectX(d.left.x)) }; })            
    .target(function(d) { return {"x": d.final_disease.y + rect_height/2,
                                  "y": typeof(d.right) !== "undefined" ? d.right.x < 0 ? d.final_disease.x : d.final_disease.x + rect_width : d.left.x < 0 ? d.final_disease.x : d.final_disease.x + rect_width }; })
    .projection(function(d) { return [d.y, d.x]; });


